sudo hostnamectl set-hostname kasm && sleep 1 && printf "\n\n  -> Please enter a password for the 'temp' account...\n" && sudo useradd -s /bin/bash -d /home/temp/ -m -G sudo temp && sleep 1 && sudo passwd temp && echo "echo Please enter a password for the 'ninja' account... && sudo usermod -l ninja sansforensics && sudo chfn -f 'ninja' ninja && sudo passwd ninja && sudo groupmod --new-name ninja sansforensics && sudo rm /home/sansforensics/.local/share/keyrings/login.keyring && gnome-session-quit --no-prompt" >> /home/temp/user.sh && sleep 1 && sudo chmod +x /home/temp/user.sh && sleep 1 && sudo reboot
